INSERT INTO `process_operator` VALUES (14,'CSV Import','This operator is used to connect a CSV file to a process.','Import','','using System;\r\nusing System.IO;\r\nusing CsvHelper;\r\nusing System.Data;\r\nusing System.Collections.Generic;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Windows.Input;\r\n\r\n\r\n    public class Script\r\n    {\r\n\r\n       public static Dictionary<string,dynamic> Execute(Dictionary<string, dynamic> input, Dictionary<string, Dictionary<string, dynamic>> parameters, bool preview)\r\n        {\r\n        \r\n        Dictionary<string, dynamic> result =  new Dictionary<string, dynamic>();\r\n        DataTable dt = new DataTable();\r\n        string    pathName = parameters[\"File Path\"][\"value\"];\r\n        \r\n        using (var stream = File.OpenRead(pathName))\r\n        {\r\n            using (var reader = new StreamReader(stream))\r\n            {\r\n                using (var csvReader = new CsvReader(reader))\r\n                {\r\n                    int i = 0;\r\n                    while (csvReader.Read())\r\n                    {\r\n                    \r\n                        if (i == 0)\r\n                        {\r\n                            foreach (var field in csvReader.FieldHeaders)\r\n                            {\r\n                                dt.Columns.Add(field);\r\n                            }\r\n                        }\r\n                        \r\n      \r\n\r\n                        DataRow row = dt.NewRow();\r\n                        \r\n                        foreach (var field in csvReader.FieldHeaders)\r\n                        {\r\n                            row[field] = csvReader.GetField(field);\r\n                        }\r\n                        \r\n                        dt.Rows.Add(row);\r\n\r\n                        i += 1;\r\n                        \r\n                        if(preview)\r\n                        {\r\n                        	continue;\r\n                        }\r\n                        \r\n                    }\r\n                }\r\n            }\r\n        }\r\n        \r\n        result.Add(\"Result\", dt);\r\n        \r\n        return result;\r\n\r\n        }\r\n\r\n    }\r\n\r\n','{\"assemblies\":[]}','{\"parameters\":{\"File Path\":{\"type\":\"fileSelect\",\"filter\":\"CSV(*.csv)|*.csv\",\"filterSelectedIndex\":\"1\",\"label\":\"File Path\",\"value\":\"\"}},\"connectors\":[{\"Name\":\"Result\",\"Type\":\"Output\",\"DataType\":\"Data Table\"}]}','False','',1),(19,'Read Excel','This operator reads an ExampleSet from the specified Excel file.','Import','','using System;\r\nusing System.IO;\r\nusing System.Collections.Generic;\r\nusing System.Data;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Windows.Input;\r\nusing Excel;\r\n\r\n\r\n    public class Script\r\n    {\r\n\r\n            public static Dictionary<string,dynamic> Execute(Dictionary<string,dynamic> input, Dictionary<string, Dictionary<string, dynamic>> parameters,bool preview)\r\n            {\r\n            \r\n            \r\n            	string    pathName = parameters[\"File Path\"][\"value\"];\r\n            	\r\n            	string h = parameters[\"Headers\"][\"value\"];\r\n            	\r\n            	Dictionary<string,dynamic> result =  new Dictionary<string,dynamic>();\r\n            \r\n		  	 	FileInfo file = new FileInfo(pathName);\r\n		        if (!file.Exists) { throw new Exception(\"Error, file doesn\'t exists!\"); }\r\n		        string extension = file.Extension;\r\n		        FileStream stream = File.Open(pathName, FileMode.Open, FileAccess.Read);\r\n		        IExcelDataReader excelReader;\r\n		        \r\n		        switch (extension)\r\n		        {\r\n			            case \".xls\":\r\n					excelReader = ExcelReaderFactory.CreateBinaryReader(stream);\r\n			                break;\r\n			            case \".xlsx\":\r\n					excelReader = ExcelReaderFactory.CreateOpenXmlReader(stream);\r\n			           