ctionary<string,dynamic> output= new Dictionary<string,dynamic>();\r\n	            \r\n	            DataTable sourceTable = (DataTable)input[\"Source\"];\r\n	            \r\n	            string column =(string)parameters[\"Column\"][\"value\"];\r\n	            \r\n	            DataTable result_out = new DataTable();\r\n	            \r\n	            System.Data.DataColumn Country = new System.Data.DataColumn(column, typeof(System.String));\r\n				Country.DefaultValue = \"0\";\r\n	                \r\n	            System.Data.DataColumn CountryMatch = new System.Data.DataColumn(\"Country Match\", typeof(System.String));\r\n				Country.DefaultValue = \"0\";\r\n	                \r\n	            System.Data.DataColumn Longitude = new System.Data.DataColumn(\"Longitude\", typeof(System.String));\r\n				Longitude.DefaultValue = \"0\";\r\n					\r\n				System.Data.DataColumn Latitude = new System.Data.DataColumn(\"Latitude\", typeof(System.String));\r\n				Latitude.DefaultValue = \"0\";\r\n	            \r\n	            result_out.Columns.Add(Country);\r\n				result_out.Columns.Add(CountryMatch);\r\n				result_out.Columns.Add(Longitude);\r\n	            result_out.Columns.Add(Latitude);\r\n	  \r\n	  			\r\n	  			\r\n	            var data_in = (((DataTable)input[\"Source\"]).AsEnumerable()).GroupBy(p => p.Field<string>(column)).Select(g => g.First()).ToList();\r\n				\r\n     \r\n	            foreach(DataRow row in data_in)\r\n	            {\r\n	            \r\n		            try\r\n		            {		  \r\n		            	string locationsRequest = CreateRequest(row[column].ToString());\r\n		                XmlDocument locationsResponse = MakeRequest(locationsRequest);\r\n		                List<string> points = ProcessResponse(locationsResponse);\r\n	\r\n		                DataRow tempRow = result_out.NewRow();\r\n		                tempRow[column] = row[column];\r\n		                tempRow[\"Country Match\"] =points[0];\r\n		                tempRow[\"Longitude\"] =points[1];\r\n		                tempRow[\"Latitude\"] =points[2];\r\n		                result_out.Rows.Add(tempRow);\r\n		            }\r\n		            catch (Exception exs)\r\n		            {\r\n		            }\r\n	            \r\n	            }\r\n	            \r\n	            output.Add(\"Result\",result_out);\r\n                   \r\n              return output;\r\n            }\r\n            \r\n\r\n        public static string CreateRequest(string queryString)\r\n        {\r\n             string UrlRequest = \"http://dev.virtualearth.net/REST/v1/Locations/\" +\r\n                                            queryString +\r\n                                            \"?output=xml\" +\r\n                                            \" &key=\" + BingMapsKey;\r\n            return (UrlRequest);\r\n        }\r\n        \r\n\r\n        public static XmlDocument MakeRequest(string requestUrl)\r\n        {\r\n            try\r\n            {\r\n                HttpWebRequest request = WebRequest.Create(requestUrl) as HttpWebRequest;\r\n                HttpWebResponse response = request.GetResponse() as HttpWebResponse;\r\n\r\n                XmlDocument xmlDoc = new XmlDocument();\r\n                xmlDoc.Load(response.GetResponseStream());\r\n                return (xmlDoc);\r\n\r\n            }\r\n            catch \r\n            {\r\n                return null;\r\n            }\r\n        }\r\n\r\n        static public List<string> ProcessResponse(XmlDocument locationsResponse)\r\n        {\r\n        	List<string> output= new List<string>();\r\n\r\n            XmlNamespaceManager nsmgr = new XmlNamespaceManager(locationsResponse.NameTable);\r\n            nsmgr.AddNamespace(\"rest\", \"http://schemas.microsoft.com/search/local/ws/rest/v1\");\r\n\r\n            XmlNodeList locationElementsForGP = locationsResponse.SelectNodes(\"//rest:Location\", nsmgr);\r\n            \r\n            \r\n            foreach (XmlNode location in locationElementsForGP)\r\n            {\r\n                XmlNodeList displayGeocodePoints = location.SelectNodes(\".//rest:GeocodePoint/rest:UsageType[.=\'Display\']/parent::node()\", nsmgr);\r\n                \r\n                output.Add(location.SelectSingleNode(\".//rest:FormattedAddress\", nsmgr).InnerText);\r\n                output.Add(location.SelectSingleNode(\".//rest:Longitude\", nsmgr).InnerText);\r\n                output.Add(location.SelectSingleNode(\".//rest:Latitude\", nsmgr).InnerText);\r\n\r\n            }\r\n            \r\n            return output;\r\n            \r\n        }\r\n            \r\n\r\n    }\r\n    \r\n \r\n  ','{\"assemblies\":[]}','{\"parameters\":{\"Column\":{\"type\":\"columnSelect\",\"label\":\"Column:\",\"value\":\"\",\"connector\":\"Source\"}},\"connectors\":[{\"Name\":\"Source\",\"Type\":\"Input\",\"DataType\":\"Data Table\"},{\"Name\":\"Result\",\"Type\":\"Output\",\"DataType\":\"Data Table\"}]}','False','',1),(21,'Join','The LEFT JOIN keyword returns all rows from the left table (table1), with the matching rows in the right table (table2).','Utility','','using System;\r\nusing System.Collections.Generic;\r\nusing System.Data;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Windows.Input;\r\n\r\n    public class Script\r\n    {\r\n\r\n\r\n	   public static Dictionary<string,dynamic> Execute(Dictionary<string,dynamic> input, Dictionary<string, Dictionary<string, dynamic>> parameters,bool preview)\r\n	   {\r\n	       DataTable left = (DataTable)input[\"Left\"];\r\n	       DataTable right = (DataTable)input[\"Right\"];\r\n	       \r\n	       DataTable resultTable;\r\n	       \r\n	       \r\n	       if (parameters[\"Join Type\"][\"value\"] == \"Left\")\r\n	       {\r\n	       		resultTable = JoinTwoDataTablesOnOneColumn(left,right,parameters[\"join_column\"][\"value\"],JoinType.Left);\r\n	       }\r\n	       else\r\n	       {\r\n	       		resultTable = JoinTwoDataTablesOnOneColumn(left,right,parameters[\"join_column\"][\"value\"],JoinType.Inner);\r\n	       \r\n	       }\r\n	       \r\n	       Dictionary<string,dynamic> result = new Dictionary<string,dynamic>();\r\n	       result.Add(\"Result\",resultTable);\r\n	       \r\n	       return result;\r\n	   }\r\n	    \r\n	    \r\n	    public enum JoinType\r\n	    {\r\n	        Inner = 0,\r\n	        Left = 1\r\n	    }\r\n	    \r\n	    public static DataTable JoinTwoDataTablesOnOneColumn(DataTable dtblLeft, DataTable dtblRight, string colToJoinOn, JoinType joinType)\r\n	    {\r\n	        string strTempColName = colToJoinOn + \"_2\";\r\n	        if (dtblRight.Columns.Contains(colToJoinOn))\r\n	            dtblRight.Columns[colToJoinOn].ColumnName = strTempColName;\r\n	\r\n	        DataTable dtblResult = dtblLeft.Clone();\r\n	\r\n	        var dt2Columns = dtblRight.Columns.OfType<DataColumn>().Select(dc => new DataColumn(dc.ColumnName, dc.DataType, dc.Expression, dc.ColumnMapping));\r\n	\r\n	        var dt2FinalColumns = from dc in dt2Columns.AsEnumerable()\r\n	                              where !dtblResult.Columns.Contains(dc.ColumnName)\r\n	                              select dc;\r\n	\r\n	        dtblResult.Columns.AddRange(dt2FinalColumns.ToArray());\r\n	\r\n	        if (!dtblLeft.Columns.Contains(colToJoinOn) || (!dtblRight.Columns.Contains(colToJoinOn) && !dtblRight.Columns.Contains(strTempColName)))\r\n	        {\r\n	            if (!dtblResult.Columns.Contains(colToJoinOn))\r\n	                dtblResult.Columns.Add(colToJoinOn);\r\n	            return dtblResult;\r\n	        }\r\n	\r\n	        switch (joinType)\r\n	        {\r\n	\r\n	            default:\r\n	            case JoinType.Inner: \r\n	                var rowDataLeftInner = from rowLeft in dtblLeft.AsEnumerable()\r\n	                                       join rowRight in dtblRight.AsEnumerable() on rowLeft[colToJoinOn] equals rowRight[strTempColName]\r\n	                                       select rowLeft.ItemArray.Concat(rowRight.ItemArray).ToArray();\r\n	\r\n	\r\n	  \r\n	                foreach (obje